require 'Set'

buffer1 = "mjqjpqmgbljsphdztnvjfqwrcgsmlb" # 7
buffer2 = "bvwbjplbgvbhsrlpgdmjqwftvncz" # 5
buffer3 = "nppdvjthqldpwncqszvftbrmjlhg" # 6
buffer4 = "nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg" # 10
buffer5 = "zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw" # 11
testBuffer = ""

startChar = 1
endChar = 4

buffer = testBuffer

while endChar <= buffer.size do
  uniqCars = Set.new
  buffer[(startChar - 1)..(endChar - 1)].split('').each do |char|
    uniqCars.add(char)
  end

  puts "UNIQCARS: #{uniqCars}"
  puts "STARTCHAR: #{startChar}"
  puts "ENDCHAR: #{endChar}"
  puts

  break if uniqCars.size == 4

  startChar += 1
  endChar += 1
end

puts endChar