require 'Set'

buffer1 = "mjqjpqmgbljsphdztnvjfqwrcgsmlb" # 19
buffer2 = "bvwbjplbgvbhsrlpgdmjqwftvncz" # 23
buffer3 = "nppdvjthqldpwncqszvftbrmjlhg" # 23
buffer4 = "nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg" # 29
buffer5 = "zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw" # 26
testBuffer = ""

buffer = testBuffer

startChar = 1
endChar = 14

while endChar <= buffer.size do
  uniqCars = Set.new
  buffer[(startChar - 1)..(endChar - 1)].split('').each do |char|
    uniqCars.add(char)
  end

  puts "UNIQCARS: #{uniqCars}"
  puts "STARTCHAR: #{startChar}"
  puts "ENDCHAR: #{endChar}"
  puts

  break if uniqCars.size == 14

  startChar += 1
  endChar += 1
end

